#!/usr/bin/env python
"""print pypi project classifiers"""
# -*- coding: utf-8 -*-
import click
import classifiers

MODULE_NAME = "classifiers"
PROG_NAME = 'python -m %s' % MODULE_NAME
USAGE = 'python -m %s name' % MODULE_NAME


@click.command()
@click.argument('name', required=True)
def _cli(name):
    _classifiers = classifiers.get(name)
    if _classifiers:
        print("\n".join(_classifiers))


if __name__ == '__main__':
    _cli(prog_name=PROG_NAME)
